mapper.select:
 			SELECT  COUNT(ID)
			FROM	MEMBER
			WHERE	NICKNAME = #{nickname}

SELECT  COUNT(ID)
FROM	MEMBER
WHERE	EMAIL = #{email}

mapper.insert:
 			INSERT INTO MEMBER(
 				ID
 				,EMAIL
 				,NICKNAME
 				,PASSWORD
 				,REGIST_DATE
 				,SALT
 			)
 			VALUES (
 				MEMBER_ID_SEQ.NEXTVAL
 				,#{email}
 				,#{nickname}
 				,#{password}
				,SYSDATE
				,#{salt} 				
 			)

mapper.select:
 			SELECT  NVL(SALT,'')
			FROM	MEMBER
			WHERE   EMAIL = #{email}
